memberdef.initializer:
namespace std {                                                            \
template <> struct hash<type>                                              \
{                                                                          \
    size_t operator()(const type &x) const                                 \
    {                                                                      \
        return std::hash<size_t>()(x._to_integral());                      \
    }                                                                      \
};                                                                         \
}
